import getpassimport bcryptfrom db import init_db, get_conndef main():    init_db()    username = input("username: ").strip()    password = getpass.getpass("password: ").strip()    pw_hash = bcrypt.hashpw(password.encode("utf-8"), bcrypt.gensalt()).decode("utf-8")    conn = get_conn()    cur = conn.cursor()    cur.execute("INSERT INTO users (username, password_hash) VALUES (?, ?)", (username, pw_hash))    conn.commit()    conn.close()    print("OK: user created")if __name__ == "__main__":    main()