i# create_user.pyimport sysimport getpassfrom auth import hash_passwordfrom db import get_conn, init_dbdef create_user(username: str, password: str):    init_db()    conn = get_conn()    cur = conn.cursor()    # 檢查是否已存在    cur.execute("SELECT 1 FROM users WHERE username = ?", (username,))    if cur.fetchone():        conn.close()        print(f"❌ 使用者 '{username}' 已存在")        return    cur.execute(        "INSERT INTO users (username, password_hash) VALUES (?, ?)",        (username, hash_password(password))    )    conn.commit()    conn.close()    print(f"✅ 使用者 '{username}' 建立完成")if __name__ == "__main__":    if len(sys.argv) >= 3:        username = sys.argv[1]        password = sys.argv[2]    else:        print("請輸入帳號")        username = input("username: ").strip()        password = getpass.getpass("password: ").strip()    if not username or not password:        print("❌ 帳號或密碼不可為空")        sys.exit(1)    create_user(username, password)